let selectedA;
let ul = document.getElementById('nav');

ul.onclick = function(event) {
  let target = event.target; 

  if (target.tagName != 'A') return; 

  highlight(target); 
};

function highlight(a) {
  if (selectedA) { 
    selectedA.classList.remove('active');
  }
  selectedA = a;
  selectedA.classList.add('active'); 
}